//change icon when hover
function hovermyaccount() {
  var x = document.getElementById("my-account_icon");
  x.setAttribute("src", "./../../image/usreG.png");
}

function unhovermyaccount() {
  var x = document.getElementById("my-account_icon");
  x.setAttribute("src", "./../../image/user2.png");
}
 
